User.delete_all
Journal.delete_all
Entry.delete_all

puts "deleting previous seed data"

puts "seeding"
users = (1..3).to_a
theme = ["love", "professional goals", "financial studies", "friendship", "happiness", "family"]
journals = (1..15).to_a
photos = ["https://www.stockvault.net/data/2007/03/01/102413/thumb16.jpg", "data:image/jpeg;base64,", "https://www.stockvault.net/data/2008/09/02/106231/thumb16.jpg"]

dates = ["Tue Dec 18 2018 11:15:29 GMT-0500 (Eastern Standard Time)", "Wed Dec 19 2018 11:32:55 GMT-0500 (Eastern Standard Time)", "Wed Dec 19 2018 10:49:47 GMT-0500 (Eastern Standard Time)", "Sun Dec 17 1995 03:24:00 GMT-0500 (Eastern Standard Time)", "Sun Dec 17 2017 03:24:00 GMT-0500 (Eastern Standard Time)", "Fri Nov 17 2017 03:24:00 GMT-0500 (Eastern Standard Time)", "Tue Oct 17 2017 03:24:00 GMT-0400 (Eastern Daylight Time)", "Sun Sep 17 2017 03:24:00 GMT-0400 (Eastern Daylight Time)"]

3.times do
  User.create(
    first_name: Faker::Name.first_name,
    last_name: Faker::Name.last_name,
    username: Faker::Name.initials,
    password: "bob",
    email: Faker::Internet.email,
    phone: Faker::PhoneNumber.cell_phone)
end


15.times do
  Journal.create(
    user_id: users.sample,
    theme: theme.sample
  )
end

100.times do
  Entry.create(
    journal_id: journals.sample,
    title: Faker::Book.title,
    body: Faker::Lorem.paragraphs(2),
    photo: photos.sample,
    location: "New York, NY",
    date: dates.sample
  )
end

puts "done seeding!"
